mapper.select:
/* BoardRepository.getList BGN */ SELECT B.BOARD_SEQ, B.BOARD_TYPE, B.TITLE, B.CONTENTS, B.REG_DATE FROM T_BOARD B
AND B.TITLE LIKE CONCAT('%', #{parameter.keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC, B.BOARD_SEQ DESC OFFSET #{pageRequest.limit} ROWS FETCH FIRST #{pageRequest.offset} ROWS ONLY /* BoardRepository.getList END */

/* BoardRepository.get BGN */
SELECT
    B.BOARD_SEQ,
    B.BOARD_TYPE,
    B.TITLE,
    B.CONTENTS,
    B.REG_DATE
FROM T_BOARD B
WHERE B.BOARD_SEQ = #{boardSeq}
/* BoardRepository.get END */

mapper.insert:
/* BoardRepository.save BGN */
INSERT INTO T_BOARD (
    BOARD_TYPE,
    TITLE,
    CONTENTS,
    REG_DATE
) VALUES (
    #{boardType.code},
    #{title},
    #{contents},
    NOW()
)
/* BoardRepository.save END */

/* BoardRepository.saveList BGN */ INSERT INTO T_BOARD ( BOARD_TYPE, TITLE, CONTENTS, REG_DATE ) VALUES
( #{board.boardType.code}, #{board.title}, #{board.contents}, NOW() )
/* BoardRepository.saveList END */

mapper.update:
/* BoardRepository.update BGN */
UPDATE T_BOARD
SET
    BOARD_TYPE = #{boardType.code},
    TITLE = #{title},
    CONTENTS = #{contents}
WHERE BOARD_SEQ = #{boardSeq}
/* BoardRepository.update END */

mapper.delete:
/* BoardRepository.delete BGN */
DELETE FROM T_BOARD
WHERE BOARD_SEQ = #{boardSeq}
/* BoardRepository.delete END */
